export interface StateInterface {
    settings: SettingsInterface;
    config: any;
    credit: {
        [address: string]: {
            amount: number;
            stake: number;
            fund: number;
            points: number;
        };
    };
    txs: {
        [id: string]: {
            status: "pending" | "dropped" | "valid" | "invalid";
            submittedAt: number;
            closesAt?: number;
            finalizedAt?: number;
            yays: string[];
            nays: string[];
            voters: string[];
        };
    };
    invocations: string[];
    foreignCalls: {
        txID: string;
        contract: string;
        input: any;
    }[];
}
export interface SettingsInterface {
    name: string;
    runtime: string;
    version: string;
    logo: string;
    foriegnContracts: {
        governance: string;
        treasury: string;
    };
    paused: boolean;
    admins: string[];
    uploader: string;
    bundleSize: number;
    gracePeriod: number;
    slashThreshold: number;
    payout: {
        kyvePerByte: number;
        idleCost: number;
    };
}
export interface ActionInterface {
    input: any;
    caller: string;
}
export interface CreditInterface {
    function: "deposit" | "withdraw" | "fund" | "unfund" | "stake" | "unstake";
    qty?: number;
}
export interface RegisterInterface {
    function: "register";
}
export interface SubmitInterface {
    function: "submit";
}
export interface UpdateInterface {
    function: "update";
    settings?: SettingsInterface;
    config?: any;
}
export interface UpdateContractsInterface {
    function: "updateContracts";
    governance?: string;
    treasury?: string;
}
